INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073ph000zu358njvqiaqm', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":13,"label":"low","title":"Низкая надёжная привязанность","description":"Близость даётся с трудом: сложно просить о поддержке, есть настороженность к откровенности.","recommendations":["Замечайте моменты, когда хочется закрыться, и мягко пробуйте «малые дозы» доверия.","Обсудите с партнёром безопасные рамки откровенности.","Рассмотрите краткосрочную терапию, ориентированную на привязанность."]},{"to":26,"label":"mid","title":"Умеренная надёжная привязанность","description":"В целом способны на близость, но временами нужны дополнительные подтверждения и пространство.","recommendations":["Практикуйте «я-сообщения» при обсуждении потребностей.","Регулярно проверяйте ожидания и границы в паре.","Поддерживайте собственные источники устойчивости (сон, режим, хобби)."]},{"to":40,"label":"high","title":"Высокая надёжная привязанность","description":"Комфортно выражаете чувства и просите о помощи, умеете сочетать близость и автономию.","recommendations":["Делитесь навыками эмпатии и регуляции в паре.","Поддерживайте ритуалы связи (совместные разговоры, встречи).","Сохраняйте баланс «я» и «мы» через личные цели."]}],"scaleKey":"secure_attachment"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073pk0011u358ebenagfd', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":8,"label":"low","title":"Низкая тревожная привязанность","description":"Редко переживаете из-за дистанции партнёра, сохраняете внутреннюю опору.","recommendations":["Продолжайте открыто говорить о чувствах без контроля.","Поддерживайте свои социальные связи помимо пары.","Фиксируйте успехи в саморегуляции (дневник, заметки)."]},{"to":17,"label":"mid","title":"Умеренная тревожность в близости","description":"Иногда включается страх отвержения и потребность в заверениях.","recommendations":["Договаривайтесь о «окнах связи» (когда и как созваниваетесь).","Освойте техники успокоения (дыхание 4-7-8, заземление).","Развивайте самоценность вне отношений."]},{"to":25,"label":"high","title":"Высокая тревожная привязанность","description":"Часто беспокоитесь о стабильности, возможна склонность к проверкам и навязчивым мыслям.","recommendations":["Составьте «план заботы о себе» на моменты ожидания ответа.","Обсудите с партнёром ясные сигналы внимания и границы.","Рассмотрите работу с психологом для переработки раннего опыта."]}],"scaleKey":"anxious_attachment"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073pn0013u358iu1avybn', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":10,"label":"low","title":"Низкая избегаюшая тенденция","description":"Готовы к близости, умеете показывать уязвимость при необходимости.","recommendations":["Поддерживайте привычку делиться переживаниями.","Сохраняйте разумный баланс личного пространства и контакта.","Отмечайте, что просьба о поддержке — это сила, а не слабость."]},{"to":21,"label":"mid","title":"Умеренная установка на дистанцию","description":"Иногда уходите в «рациональность» и паузы, чтобы не чувствовать уязвимость.","recommendations":["Обозначайте партнёру, когда нужна пауза и когда вернётесь к разговору.","Тренируйте короткие фразы признания чувств («мне волнительно…»).","Планируйте регулярные моменты тепла и близости."]},{"to":32,"label":"high","title":"Высокая избегаюшая привязанность","description":"Склонность к уходу от эмоциональной близости, контролю и самостоятельности любой ценой.","recommendations":["Исследуйте убеждения про уязвимость (дневник, терапия).","Практикуйте «дозированное» открывание — 1 факт/день о себе.","Создайте совместные ритуалы (время, прикосновения) с низким риском."]}],"scaleKey":"avoidant_attachment"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073pq0015u358bezlt698', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":20,"label":"low","title":"Поддержка не в приоритете","description":"Вы меньше ориентируетесь на эмоциональную опору как ключевую ценность.","recommendations":["Проверьте, какие формы поддержки всё же приятны.","Обсудите с партнёром различия в ожиданиях поддержки.","Отмечайте моменты, когда поддержка повышает близость."]},{"to":40,"label":"mid","title":"Баланс поддержки","description":"Цените понимание, но важен и другой набор ценностей.","recommendations":["Договоритесь о понятных способах поддержки в стрессе.","Практикуйте активное слушание раз в неделю.","Уточняйте, чего именно ждёте в сложные моменты."]},{"to":60,"label":"high","title":"Поддержка — ключевая ценность","description":"Эмоциональная близость и принятие — основа гармонии в отношениях.","recommendations":["Создайте ритуал «15 минут откровенности» ежедневно.","Учитесь просить поддержку конкретно («мне поможет…»).","Берегите партнёра от эмоционального выгорания — чередуйте роли."]}],"scaleKey":"value_support"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073ps0017u3587yujiu43', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":23,"label":"low","title":"Страсть не на первом месте","description":"Романтическая искра для вас вторична по сравнению с другими ценностями.","recommendations":["Запланируйте «микро-романтику» (5–10 мин) несколько раз в неделю.","Ищите эмоциональную новизну через общие мини-приключения.","Обсудите язык флирта, комфортный обоим."]},{"to":46,"label":"mid","title":"Умеренная ценность страсти","description":"Важны и эмоции, и стабильность — стремитесь к балансу.","recommendations":["Чередуйте спокойные и драйвовые свидания.","Отмечайте, что усиливает притяжение (одежда, обстановка, темы).","Открыто говорите о желаниях и границах."]},{"to":70,"label":"high","title":"Страсть — главный драйвер","description":"Яркость, спонтанность и химия сильно влияют на удовлетворённость.","recommendations":["Согласуйте «правила безопасности» для экспериментов.","Регулярно вносите новизну (места, впечатления, роли).","Поддерживайте эмоциональную связь, а не только интенсивность."]}],"scaleKey":"value_passion"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073pu0019u358y8senuph', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":21,"label":"low","title":"Безопасность вторична","description":"Готовы к риску и неопределённости, меньше нуждаетесь в чётких правилах.","recommendations":["Согласуйте базовые границы (финансы, верность, быт).","Фиксируйте договорённости письменно, чтобы снизить споры.","Оценивайте риски, касающиеся обоих."]},{"to":43,"label":"mid","title":"Баланс безопасности","description":"Цените надёжность, но допускаете гибкость и спонтанность.","recommendations":["Проводите ежемесячные «перепроверки» правил и ожиданий.","Создайте финансовую/бытовую «подушку» для спокойствия.","Уточняйте, что именно даёт ощущение защищённости."]},{"to":65,"label":"high","title":"Безопасность — в приоритете","description":"Предсказуемость, ясные рамки и стабильность — основа вашего спокойствия.","recommendations":["Делитесь триггерами тревоги и способами её снизить.","Планируйте, прежде чем менять важные договорённости.","Удерживайте гибкость: оставляйте «окно» для новизны."]}],"scaleKey":"value_security"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073pw001bu358kkryxouu', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":21,"label":"low","title":"Рост не центральная ценность","description":"Совместное развитие важно постольку-поскольку, приоритет — другие сферы.","recommendations":["Найдите одно малое общее направление роста (книга, курс).","Поддерживайте интерес партнёра к его целям.","Обсуждайте планы на квартал в формате «что даёт радость».","Фиксируйте маленькие достижения пары."]},{"to":43,"label":"mid","title":"Умеренный акцент на росте","description":"Цените развитие, но бережёте баланс с бытом и отдыхом.","recommendations":["Ставьте 1–2 общих SMART-цели на месяц.","Планируйте «учебные свидания» (лекция, мастер-класс).","Раз в месяц подводите итоги и корректируйте курс."]},{"to":65,"label":"high","title":"Рост — сильная ценность","description":"Вам важно учиться и расширять возможности вместе.","recommendations":["Согласуйте темп, чтобы не перегружать друг друга.","Комбинируйте рост с заботой и отдыхом.","Празднуйте результаты и укрепляйте идентичность пары как «команды развития»."]}],"scaleKey":"value_growth"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073pz001du3582b4i622c', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":21,"label":"low","title":"Слова — не главный язык","description":"Комплименты и вербализация важны ограниченно.","recommendations":["Сообщите партнёру, какой формат слов приятен.","Используйте короткие признания в ключевые моменты.","Комбинируйте слова с действиями."]},{"to":43,"label":"mid","title":"Умеренная потребность в словах","description":"Нужны тёплые слова, но не как единственный канал любви.","recommendations":["Введите ежедневный мини-ритуал благодарности.","Попросите партнёра о конкретных фразах поддержки.","Избегайте критики в уязвимые моменты."]},{"to":65,"label":"high","title":"Слова — ведущий язык любви","description":"Вербальные подтверждения сильно усиливают чувство близости.","recommendations":["Практикуйте комплименты и «заметки любви».","Просите о словах поддержки, когда это нужно.","Сочетайте слова с активным слушанием."]}],"scaleKey":"language_words"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073q0001fu358bvh12t2n', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":23,"label":"low","title":"Совместное время не критично","description":"Цените автономию, а встречи — по необходимости.","recommendations":["Планируйте меньше, но качественнее.","Согласуйте «безгаджетные» интервалы.","Ищите точки соприкосновения в расписании."]},{"to":46,"label":"mid","title":"Нужен баланс времени вместе","description":"Важны регулярные свидания без перегруза.","recommendations":["Назначьте еженедельный вечер пары.","Раз в месяц — особое свидание.","Обсуждайте ожидания по длительности и формату."]},{"to":70,"label":"high","title":"Время — главный язык любви","description":"Чувствуете любовь через присутствие и фокус на вас.","recommendations":["Сделайте приоритетом совместный календарь.","Убирайте отвлекающие факторы во время встреч.","Создайте ритуалы начала и завершения дня."]}],"scaleKey":"language_time"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073q3001hu3582lt9khak', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":20,"label":"low","title":"Подарки не про вас","description":"Материальные знаки внимания вторичны.","recommendations":["Заменяйте подарки символическими жестами.","Обсудите бюджет и ожидания на праздники.","Фокус на впечатлениях вместо вещей."]},{"to":40,"label":"mid","title":"Умеренная значимость подарков","description":"Приятно получать знаки внимания в особые моменты.","recommendations":["Соберите «лист радостей» друг друга.","Дарите малые, но осмысленные сюрпризы.","Подкрепляйте подарки словами и временем."]},{"to":60,"label":"high","title":"Подарки — ведущий язык любви","description":"Осязаемые символы заботы делают вас счастливее.","recommendations":["Планируйте «микро-сюрпризы» в обычные дни.","Согласуйте границы стоимости.","Добавляйте личные смыслы (записки, истории)."]}],"scaleKey":"language_gifts"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073q6001ju3582admy4bb', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":23,"label":"low","title":"Дела — не главный канал","description":"Помощь важна, но не ключ к близости.","recommendations":["Определите 1–2 зоны, где помощь особенно ценна.","Говорите спасибо за конкретные действия.","Не обесценивайте заботу партнёра."]},{"to":46,"label":"mid","title":"Нужны понятные жесты заботы","description":"Практическая помощь повышает ощущение любви.","recommendations":["Составьте список дел, которые реально снимают нагрузку.","Внедрите «дежурства заботы».","Обсудите границы между помощью и контролем."]},{"to":70,"label":"high","title":"Забота действиями — ведущий язык","description":"Поступки важнее слов: помощь — главный маркер любви.","recommendations":["Просите о конкретной помощи без намёков.","Отмечайте вклад партнёра регулярно.","Планируйте взаимные акты заботы."]}],"scaleKey":"language_service"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073q8001lu358n2ndjsqj', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":20,"label":"low","title":"Прикосновения вторичны","description":"Телесный контакт не обязателен для чувства любви.","recommendations":["Обозначьте комфортные форматы телесности.","Компенсируйте теплом слов и дел.","Уважайте разные уровни телесного комфорта."]},{"to":40,"label":"mid","title":"Умеренная потребность в прикосновениях","description":"Нужны объятия и нежность, но в разумной дозе.","recommendations":["Согласуйте частоту и уместность прикосновений.","Создайте вечерние ритуалы телесной близости.","Говорите о предпочтениях открыто."]},{"to":60,"label":"high","title":"Прикосновения — главный язык","description":"Тактильность критична для ощущения любви и безопасности.","recommendations":["Добавляйте нежность в бытовые моменты.","Обсудите границы и «зоны комфорта».","Сочетайте телесность с вербальной поддержкой."]}],"scaleKey":"language_touch"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073qb001nu3583sihd9lz', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":3,"label":"low","title":"Редко идёте в сотрудничество","description":"В конфликте чаще выбираете другие стратегии.","recommendations":["Пробуйте совместный поиск вариантов «мы против проблемы».","Используйте перефразирование для понимания позиций.","Фиксируйте общее «зачем» решения."]},{"to":7,"label":"mid","title":"Сотрудничаете при необходимости","description":"Стремитесь учитывать интересы обоих, но не всегда.","recommendations":["Задавайте вопрос: «Какое решение устроит нас обоих?»","Разделяйте обсуждение людей и задач.","Ведите протокол договорённостей."]},{"to":10,"label":"high","title":"Сотрудничество — ваш стиль","description":"Ищете win-win и готовы вкладываться в диалог.","recommendations":["Следите, чтобы процесс не затягивался.","Помогайте партнёру формулировать интересы, не позиции.","Закрепляйте решения действиями и сроками."]}],"scaleKey":"conflict_collab"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073qd001pu358ufxgufbj', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":5,"label":"low","title":"Мало избегания","description":"Готовы обсуждать трудные темы без откладывания.","recommendations":["Сохраняйте уважительную прямоту.","Планируйте время и формат сложных разговоров.","Используйте паузы как инструмент, а не уход."]},{"to":10,"label":"mid","title":"Умеренное избегание","description":"Иногда берёте паузу, чтобы остыть — это может быть полезно.","recommendations":["Договаривайтесь о длительности паузы и времени возврата.","Отмечайте, что нужно, чтобы вернуться к разговору.","Фиксируйте тему, чтобы не терять фокус."]},{"to":15,"label":"high","title":"Высокая тенденция к избеганию","description":"Сложные темы часто откладываются, что копит напряжение.","recommendations":["Начинайте с маленьких, безопасных обсуждений.","Используйте шаблоны «я-сообщений».","Задумайтесь о поддержке медиатора/терапевта при острых спорах."]}],"scaleKey":"conflict_avoid"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073qg001ru358nrvmcupk', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":3,"label":"low","title":"Редко идёте на компромисс","description":"Чаще выбираете другие стратегии урегулирования.","recommendations":["Ищите зоны, где уступки малозатратны.","Определяйте «красные линии» до обсуждения.","Меняйтесь уступками эквивалентно."]},{"to":7,"label":"mid","title":"Компромисс периодически","description":"Готовы к обмену уступками, когда это быстро решает вопрос.","recommendations":["Используйте правило «малый шаг за малый шаг».","Фиксируйте временные компромиссы и дату пересмотра.","Следите, чтобы компромисс не подменял сотрудничество."]},{"to":10,"label":"high","title":"Компромисс — сильная сторона","description":"Умеете быстро находить «середину», снижая напряжение.","recommendations":["Оценивайте, не теряются ли важные потребности.","Чередуйте компромисс с совместным поиском решений.","Проверяйте удовлетворённость обоих спустя время."]}],"scaleKey":"conflict_comprom"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073qj001tu358r3v2k72p', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":3,"label":"low","title":"Редко уступаете","description":"Склонны отстаивать своё, что защищает границы.","recommendations":["Отмечайте, где уступка принесёт больше пользы.","Развивайте эмпатию к потребностям партнёра.","Держите канал обратной связи открытым."]},{"to":7,"label":"mid","title":"Готовность к уступкам","description":"Иногда ставите интересы партнёра выше своих.","recommendations":["Следите за балансом, чтобы не копить обиды.","Проговаривайте, что именно отдаёте и ради чего.","Просите взаимных шагов навстречу."]},{"to":10,"label":"high","title":"Высокая приспособляемость","description":"Часто уступаете, рискуя игнорировать собственные потребности.","recommendations":["Определите личные «красные линии».","Практикуйте уверенные отказы без вины.","Ищите баланс: уступка сегодня — встречный шаг завтра."]}],"scaleKey":"conflict_accom"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
INSERT INTO rules (id, test_id, kind, priority, payload, created_at, updated_at) VALUES ('cmh0073ql001vu358wfrjpapm', 'cmgyzeklz0000u3l09cwl94v2', 'threshold', 100, '{"ranges":[{"to":3,"label":"low","title":"Низкое соперничество","description":"Редко настаиваете на своём любой ценой — это снижает эскалации.","recommendations":["Сохраняйте конструктивность при защите интересов.","Выбирайте битвы: где принципиально, где — нет.","Подкрепляйте твёрдость эмпатией."]},{"to":7,"label":"mid","title":"Умеренное соперничество","description":"Иногда твердо отстаиваете позицию — полезно при ограниченных ресурсах.","recommendations":["Описывайте интересы, а не нападайте на личность.","Предлагайте альтернативы при жёстких требованиях.","Договаривайтесь о правилах спора заранее."]},{"to":10,"label":"high","title":"Высокая конкурентность","description":"Часто добиваетесь своего, что может ранить партнёра и связь.","recommendations":["Сначала валидируйте чувства, потом аргументы.","Ограничьте время «жёстких» дискуссий.","Ищите win-win, когда ставки невысоки."]}],"scaleKey":"conflict_compete"}', '2025-10-23T06:40:20.248Z', '2025-10-23T06:40:20.248Z');
